using System; using System.Globalization;  namespace deliverable_one {     class Program     {         static void Main(string[] args)         {             Console.WriteLine("Enter a US dollar amount to be converted into JPY, SEK, and THB.");             double usd = double.Parse(Console.ReadLine());              int japeneseToUsd;             japeneseToUsd = Convert.ToInt32((int)usd * 111.26);             Console.WriteLine("USD to Japenese Yen = " + (japeneseToUsd.ToString("C", CultureInfo.CreateSpecificCulture("ja-jp"))));              double swedishToUsd;             swedishToUsd = (int)usd * 9.3;             Console.WriteLine("USD to Swedish Krona= " + swedishToUsd.ToString("C", CultureInfo.CreateSpecificCulture("sv-se")));              double thaiToUsd;             thaiToUsd = (int)usd * 31.62;             Console.WriteLine("USD to Thai Baht = " + thaiToUsd.ToString("C", CultureInfo.CreateSpecificCulture("th-TH")));              double total = japeneseToUsd + swedishToUsd + thaiToUsd;             Console.WriteLine("The total of all the currenices in USD is $" + total);              double avg;             avg = Convert.ToInt32((int)japeneseToUsd + swedishToUsd + thaiToUsd / 3);             Console.WriteLine("The average is " + avg);              double maximum = usd;             double minimum = usd;              if (japeneseToUsd > swedishToUsd && japeneseToUsd > thaiToUsd)             {                 maximum = japeneseToUsd;             }             else if (swedishToUsd > japeneseToUsd && swedishToUsd > thaiToUsd)             {                 maximum = swedishToUsd;             }             else if (thaiToUsd > japeneseToUsd && thaiToUsd > swedishToUsd)             {                 maximum = thaiToUsd;             }             else             {                 Console.WriteLine("There is an error");             }              if (japeneseToUsd < swedishToUsd && japeneseToUsd < thaiToUsd)             {                 minimum = japeneseToUsd;             }             else if (swedishToUsd < japeneseToUsd && swedishToUsd < thaiToUsd)             {                 minimum = swedishToUsd;             }             else if (thaiToUsd < japeneseToUsd && thaiToUsd < swedishToUsd)             {                 minimum = thaiToUsd;             }             else             {                 Console.WriteLine("There is an error");             }               Console.WriteLine("The maximum value is " + maximum);             Console.WriteLine("The minimum value is " + minimum);         }     } }  